import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom'; // Import Link for navigation
import { fetchHotels } from '../services/hotelApi'; // Importing deleteHotelById
import { HotelData } from '../types/Hotels'; // Import HotelManagement type

const AdminHotelPage: React.FC = () => {
  const [hotels, setHotels] = useState<HotelData[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string>('');

  // Fetch the list of hotels when the component mounts
  useEffect(() => {
    const loadHotels = async () => {
      try {
        const hotelData = await fetchHotels();
        setHotels(hotelData);
        setLoading(false);
      } catch (error: any) {
        console.error('Error fetching hotels:', error);
        setError('Failed to load hotels');
        setLoading(false);
      }
    };

    loadHotels();
  }, []);  

  // Handle delete hotel
  const deleteHotel = async (id: number) => {
    try {
      await deleteHotel(id); // Call API to delete hotel
      setHotels(hotels.filter((hotel) => hotel.id !== id)); // Remove the deleted hotel from state
    } catch (error: any) {
      console.error('Error deleting hotel:', error);
      setError('Failed to delete hotel');
    }
  };

  // Handle edit hotel (This could open a modal or navigate to an edit page)
  const editHotel = (id: number) => {
    // Redirect to an edit page or open a modal for editing hotel details
    console.log(`Editing hotel with id: ${id}`);
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold text-gray-900">Admin - Manage Hotels</h1>

      <Link to="/admin/hotels/create" className="bg-green-500 text-white p-2 rounded mt-4 inline-block">
        Create New Hotel
      </Link>

      <div className="mt-6">
        {loading ? (
          <p>Loading hotels...</p>
        ) : error ? (
          <p className="text-red-600">{error}</p>
        ) : hotels.length === 0 ? (
          <p>No hotels available at the moment.</p>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
            {hotels.map((hotel) => (
              <div key={hotel.id} className="border p-4 rounded-lg">
                <h2 className="text-xl font-semibold">{hotel.name}</h2>
                <p>{hotel.address}</p>
                <p className="text-gray-500">Rating: {hotel.averageRating || 'N/A'}</p>
                <div className="mt-4 flex justify-between">
                  <button
                    className="bg-blue-500 text-white py-1 px-4 rounded"
                    onClick={() => editHotel(hotel.id)}
                  >
                    Edit
                  </button>
                  <button
                    className="bg-red-500 text-white py-1 px-4 rounded"
                    onClick={() => deleteHotel(hotel.id)}
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminHotelPage;

import React, { useEffect, useState } from "react";
import { CreditCard } from "lucide-react";
import { getAllPayments } from "../services/paymentApi";
import { Payment } from "../types/Payment";
import { getBookingById } from "../services/bookingApi";

const ITEMS_PER_PAGE = 5;

const AdminPaymentsPage: React.FC = () => {
  const [payments, setPayments] = useState<Payment[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [bookingDetails, setBookingDetails] = useState<{ [key: number]: any }>({});

  useEffect(() => {
    const fetchPayments = async () => {
      try {
        const data = await getAllPayments();
        setPayments(data);

        // Fetch booking details for each payment
        const bookingDetailsPromises = data.map(async (payment) => {
          const bookingData = await getBookingById(payment.bookingId);
          return { bookingId: payment.bookingId, bookingData };
        });

        const bookingResults = await Promise.all(bookingDetailsPromises);

        // Map booking details by payment's bookingId
        const bookingMap: { [key: number]: any } = {};
        bookingResults.forEach((result) => {
          bookingMap[result.bookingId] = result.bookingData;
        });
        setBookingDetails(bookingMap);
      } catch (err: any) {
        setError("Failed to fetch payments.");
      } finally {
        setLoading(false);
      }
    };

    fetchPayments();
  }, []);

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
    });
  };

  const totalPages = Math.ceil(payments.length / ITEMS_PER_PAGE);
  const currentPayments = payments.slice(
    (currentPage - 1) * ITEMS_PER_PAGE,
    currentPage * ITEMS_PER_PAGE
  );

  const goToPage = (page: number) => {
    if (page >= 1 && page <= totalPages) {
      setCurrentPage(page);
    }
  };

  if (loading) return <div className="p-6 text-white">Loading payments...</div>;
  if (error) return <div className="p-6 text-red-500">{error}</div>;

  return (
    <div className="p-6 bg-gray-900 text-white min-h-full">
      <h1 className="text-2xl mb-4">All Payments</h1>

      {currentPayments.length === 0 ? (
        <div>No payments found.</div>
      ) : (
        <ul className="space-y-4">
          {currentPayments.map((payment) => {
            const booking = bookingDetails[payment.bookingId];
            return (
              <li
                key={payment.id}
                className="flex items-center justify-between gap-4 p-4 rounded bg-gray-800 text-sm"
              >
                <div className="flex items-center gap-3">
                  <CreditCard size={20} />
                  <span><strong>Booking:</strong> {payment.bookingId}</span>
                  <span><strong>Amount:</strong> ${payment.amount.toFixed(2)}</span>
                  <span className="flex items-center gap-1 text-sm">
                    <strong>Status:</strong>
                    <span
                      className={`text-xs font-semibold px-2 py-0.5 rounded-full ${
                        payment.status === "SUCCESS"
                          ? "bg-green-200 text-green-800"
                          : payment.status === "FAILURE"
                          ? "bg-red-200 text-red-800"
                          : "bg-yellow-200 text-yellow-800"
                      }`}
                    >
                      {payment.status}
                    </span>
                  </span>
                </div>
                <div className="text-xs text-gray-400">
                  {formatDate(payment.transactionDate)}
                </div>
                {booking && (
                  <div className="mt-2 text-sm text-gray-400">
                    <p><strong>Check-In Date:</strong> {formatDate(booking.checkInDate)}</p>
                    <p><strong>Check-Out Date:</strong> {formatDate(booking.checkOutDate)}</p>
                  </div>
                )}
              </li>
            );
          })}
        </ul>
      )}

      {totalPages > 1 && (
        <div className="flex justify-center mt-6 space-x-2">
          <button
            onClick={() => goToPage(currentPage - 1)}
            disabled={currentPage === 1}
            className="px-3 py-1 bg-gray-700 hover:bg-gray-600 rounded disabled:opacity-50"
          >
            Prev
          </button>

          {Array.from({ length: totalPages }, (_, i) => i + 1)
            .filter((page) => {
              return (
                page === 1 ||
                page === totalPages ||
                Math.abs(page - currentPage) <= 1
              );
            })
            .reduce<(number | string)[]>((acc, page, index, pages) => {
              if (index > 0 && (page as number) - (pages[index - 1] as number) > 1) {
                acc.push('...');
              }
              acc.push(page);
              return acc;
            }, [])
            .map((item, index) => (
              <button
                key={index}
                onClick={() => typeof item === 'number' && goToPage(item)}
                disabled={item === '...'}
                className={`px-3 py-1 rounded ${
                  item === currentPage
                    ? 'bg-blue-600'
                    : 'bg-gray-700 hover:bg-gray-600'
                } ${item === '...' && 'cursor-default text-gray-400'}`}
              >
                {item}
              </button>
            ))}

          <button
            onClick={() => goToPage(currentPage + 1)}
            disabled={currentPage === totalPages}
            className="px-3 py-1 bg-gray-700 hover:bg-gray-600 rounded disabled:opacity-50"
          >
            Next
          </button>
        </div>
      )}

    </div>
  );
};

export default AdminPaymentsPage;

import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import HotelForm from '../components/Hotel/HotelForm';
import { createHotel, fetchHotelById, updateHotel } from '../services/hotelApi';
import MessageModal from '../components/MessageModal'; // Make sure the path is correct

const CreateUpdateHotelPage: React.FC = () => {
  const { id } = useParams<{ id?: string }>();
  const navigate = useNavigate();
  const [hotelData, setHotelData] = useState<any>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalMessage, setModalMessage] = useState('');
  const [modalType, setModalType] = useState<'success' | 'error'>('success'); // Default to success

  useEffect(() => {
    if (id) {
      const fetchHotelData = async () => {
        try {
          const data = await fetchHotelById(Number(id));
          setHotelData(data);
        } catch (error) {
          console.error('Failed to fetch hotel data:', error);
          setModalMessage('Failed to fetch hotel data.');
          setModalType('error');
          setIsModalOpen(true);
        }
      };
      fetchHotelData();
    }
  }, [id]);

  const handleSaveHotel = async (formData: FormData) => {
    try {
      if (id) {
        await updateHotel(Number(id), formData);
        setModalMessage('Hotel updated successfully!');
        setModalType('success');
      } else {
        await createHotel(formData);
        setModalMessage('Hotel created successfully!');
        setModalType('success');
      }

      setIsModalOpen(true);

      setTimeout(() => {
        setIsModalOpen(false);
        navigate('/');
      }, 3000);
    } catch (error) {
      console.error('Failed to save hotel:', error);
      setModalMessage('Failed to save hotel data.');
      setModalType('error');
      setIsModalOpen(true);
    }
  };  

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <div className="w-full max-w-4xl p-8 bg-white shadow-lg rounded-lg">
        <HotelForm onSave={handleSaveHotel} hotelData={hotelData} hotelId={Number(id)} />
      </div>
      <MessageModal 
        isOpen={isModalOpen} 
        onClose={closeModal} 
        message={modalMessage} 
        type={modalType} 
      />
    </div>
  );
};

export default CreateUpdateHotelPage;

import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import HotelCard from '../components/Hotel/HotelCard';
import SearchBar from '../components/SearchBar';
import { useNotificationContext } from "../contexts/NotificationContext";
import { fetchHotels } from '../services/hotelApi';
import { getReviewsForHotel } from '../services/ratingApi';
import { HotelData } from '../types/Hotels';

const HomePage: React.FC = () => {
  const [hotels, setHotels] = useState<HotelData[]>([]);
  const [filteredHotels, setFilteredHotels] = useState<HotelData[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string>('');
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [hotelsPerPage] = useState<number>(8);
  const { refreshNotifications } = useNotificationContext();

  const userId = sessionStorage.getItem('userId');
  const userRole = sessionStorage.getItem('role');
  const isAdmin = userId && userRole === 'admin';

  const navigate = useNavigate();

  useEffect(() => {
    refreshNotifications();

    const loadHotels = async () => {
      try {
        const hotelData = await fetchHotels();

        if (Array.isArray(hotelData)) {
          const hotelsWithRatings = await Promise.all(
            hotelData.map(async (hotel) => {
              const reviews = await getReviewsForHotel(hotel.id);
              const averageRating =
                reviews.length > 0
                  ? reviews.reduce((sum, review) => sum + review.rating, 0) / reviews.length
                  : 0;

              return { ...hotel, averageRating };
            })
          );

          setHotels(hotelsWithRatings);
          setFilteredHotels(hotelsWithRatings);
        } else {
          throw new Error('Invalid data format received');
        }

        setLoading(false);
      } catch (error: any) {
        console.error('Error fetching hotels:', error);
        setError('Failed to load hotels. Please try again.');
        setLoading(false);
      }
    };

    loadHotels();
  }, []);

  const handleSearch = (query: string) => {
    if (!query.trim()) {
      setFilteredHotels(hotels);
      return;
    }

    const lowercasedQuery = query.toLowerCase();
    const filtered = hotels.filter((hotel) =>
      hotel.name.toLowerCase().includes(lowercasedQuery)
    );
    setFilteredHotels(filtered);
  };

  const handleCreateHotel = () => {
    navigate('/create-hotel');
  };

  // Get the hotels to display on the current page
  const indexOfLastHotel = currentPage * hotelsPerPage;
  const indexOfFirstHotel = indexOfLastHotel - hotelsPerPage;
  const currentHotels = filteredHotels.slice(indexOfFirstHotel, indexOfLastHotel);

  // Handle page change
  const paginate = (pageNumber: number) => setCurrentPage(pageNumber);

  // Handle next and previous page changes
  const nextPage = () => {
    if (currentPage < totalPages) setCurrentPage(currentPage + 1);
  };

  const prevPage = () => {
    if (currentPage > 1) setCurrentPage(currentPage - 1);
  };

  // Calculate total pages
  const totalPages = Math.ceil(filteredHotels.length / hotelsPerPage);

  return (
    <div className="p-6">
      <div className="relative bg-white py-16 px-6 md:px-20 rounded-lg">
        {/* Centered Content */}
        <div className="text-center max-w-3xl mx-auto">
          <h1 className="text-4xl md:text-5xl font-bold text-gray-900 mb-6">
            Welcome to StayMate hotel
          </h1>
          {/* Search Bar centered */}
          <div className="w-full max-w-xl mx-auto">
            <SearchBar onSearch={handleSearch} />
          </div>
        </div>
          {/* Create Hotel button - bottom right */}
          {isAdmin && (
            <button
              onClick={handleCreateHotel}
              className="fixed bottom-6 right-6 bg-blue-600 text-white px-4 py-3 rounded-full shadow-lg hover:bg-blue-700 hover:scale-105 transition-all z-50"
            >
              Create Hotel
            </button>
          )}
      </div>
      <div>
        <h2 className="text-2xl font-semibold text-gray-800 mb-4">Recommended Hotels</h2>

        {loading ? (
          <p>Loading hotels...</p>
        ) : error ? (
          <p className="text-red-600">{error}</p>
        ) : hotels.length === 0 ? (
          <p>No hotels available at the moment.</p>
        ) : (
          <>
            <div className="grid grid-cols-2 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-x-2 gap-y-3">
              {currentHotels.map((hotel) => (
                <HotelCard key={hotel.id} hotel={hotel} />
              ))}
            </div>

            {/* Pagination with Arrows */}
            <div className="flex justify-center mt-4 items-center gap-2">
              <button
                onClick={prevPage}
                disabled={currentPage === 1}
                className={`px-4 py-2 rounded-md ${currentPage === 1 ? 'bg-gray-300 text-gray-800' : 'bg-blue-600 text-white'}`}
              >
                &lt;
              </button>
              <div className="flex gap-2">
                {Array.from({ length: totalPages }, (_, index) => (
                  <button
                    key={index + 1}
                    onClick={() => paginate(index + 1)}
                    className={`px-4 py-2 rounded-md ${
                      currentPage === index + 1 ? 'bg-blue-600 text-white' : 'bg-gray-300 text-gray-800'
                    }`}
                  >
                    {index + 1}
                  </button>
                ))}
              </div>
              <button
                onClick={nextPage}
                disabled={currentPage === totalPages}
                className={`px-4 py-2 rounded-md ${currentPage === totalPages ? 'bg-gray-300 text-gray-800' : 'bg-blue-600 text-white'}`}
              >
                &gt;
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default HomePage;

import { useEffect, useState } from "react";
import { FaStar } from "react-icons/fa";
import { useNavigate, useParams } from "react-router-dom";
import { ClipLoader } from "react-spinners";

import ConfirmationModal from "../components/ConfirmationModal";
import HotelDetails from "../components/Hotel/HotelDetails";
import MessageModal from "../components/MessageModal";
import ReviewModal from "../components/ReviewModal";

import { deleteHotel, fetchHotelById } from "../services/hotelApi";
import { getReviewsForHotel } from "../services/ratingApi";
import { getUserInfo } from "../services/userApi";
import { addBookmark, getBookmarkedHotelIds, removeBookmark } from "../services/bookmarkApi";

import { HotelData } from "../types/Hotels";
import { Review } from "../types/Review";

const useHotelData = (id: string) => {
  const [loading, setLoading] = useState(true);
  const [hotel, setHotel] = useState<HotelData | null>(null);
  const [reviews, setReviews] = useState<Review[]>([]);
  const [userInfo, setUserInfo] = useState<{ [key: string]: { firstName: string; lastName: string } }>({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const hotelData = await fetchHotelById(Number(id));
        const reviewsData = await getReviewsForHotel(Number(id));

        const userInfoMap = await Promise.all(
          reviewsData.map(async (review) => {
            const user = await getUserInfo(String(review.userId));
            return { userId: review.userId, userInfo: user.user };
          })
        ).then((data) =>
          data.reduce((acc, { userId, userInfo }) => ({ ...acc, [userId]: userInfo }), {})
        );

        setHotel(hotelData);
        setReviews(reviewsData);
        setUserInfo(userInfoMap);
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  return { loading, hotel, reviews, userInfo, setReviews, setUserInfo };
};

const renderStars = (rating: number) => {
  const fullStars = Math.floor(rating);
  const halfStar = rating % 1 !== 0;

  return (
    <>
      {[...Array(fullStars)].map((_, i) => (
        <FaStar key={i} className="text-yellow-500" />
      ))}
      {halfStar && <FaStar className="text-yellow-500 opacity-50" />}
    </>
  );
};

const HotelDetailsPage = () => {
  const { id } = useParams<{ id: string }>();
  const { loading, hotel, reviews, userInfo, setReviews, setUserInfo } = useHotelData(id!);
  const [modalMessage, setModalMessage] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [hotelToDelete, setHotelToDelete] = useState<number | null>(null);
  const [isBookmarked, setIsBookmarked] = useState(false);
  const [messageModalOpen, setMessageModalOpen] = useState(false);
  const [messageModalType, setMessageModalType] = useState<"success" | "error">("success");
  const [messageModalContent, setMessageModalContent] = useState("");
  const [isReviewModalOpen, setIsReviewModalOpen] = useState(false);
  const navigate = useNavigate();

  const currentUserId = Number(sessionStorage.getItem("userId"));

  const formatToAMPM = (timeString: string) => {
    const [hours, minutes] = timeString.split(":").map(Number);
    const period = hours >= 12 ? "PM" : "AM";
    const formattedHour = hours % 12 || 12;
    const formattedMinute = minutes < 10 ? `0${minutes}` : minutes;
    return `${formattedHour}:${formattedMinute} ${period}`;
  };

  const getPricingRange = () => {
    if (!hotel?.rooms?.length) return "$0 - $0";
    const prices = hotel.rooms.map((room) => room.pricePerNight);
    return `$${Math.min(...prices)} - $${Math.max(...prices)}`;
  };

  const handleDeleteHotel = (hotelId: number) => {
    setHotelToDelete(hotelId);
    setModalMessage("Are you sure you want to delete this hotel?");
    setIsModalOpen(true);
  };

  const confirmDeletion = async () => {
    if (hotelToDelete) {
      try {
        await deleteHotel(hotelToDelete);
        setIsModalOpen(false);
        setMessageModalContent("Hotel deleted successfully!");
        setMessageModalType("success");
        setMessageModalOpen(true);
        setTimeout(() => {
          setMessageModalOpen(false);
          navigate("/");
        }, 2000);
      } catch (error) {
        console.error("Error deleting hotel:", error);
        setIsModalOpen(false);
        setMessageModalContent("There was an error deleting the hotel.");
        setMessageModalType("error");
        setMessageModalOpen(true);
      }
    }
  };

  const handleBookmarkToggle = async () => {
    if (!currentUserId || !hotel) return;

    if (isBookmarked) {
      await removeBookmark(currentUserId, hotel.id);
      setIsBookmarked(false);
    } else {
      await addBookmark(currentUserId, hotel.id);
      setIsBookmarked(true);
    }
  };

  const handleCloseReviewModal = () => {
    setIsReviewModalOpen(false);
  };

  const handleReviewSubmitted = async (review: Review | null) => {
    if (!review) return;

    // First, update the reviews state with the new review
    setReviews((prev) => [...prev, review]);

    // Fetch the user info for the reviewer
    try {
      const user = await getUserInfo(String(review.userId));

      // Update the userInfo state with the new user info
      setUserInfo((prevUserInfo) => ({
        ...prevUserInfo,
        [review.userId]: user.user,
      }));
    } catch (error) {
      console.error("Error fetching user info:", error);
    }

    // Close the review modal
    handleCloseReviewModal();
  };


  useEffect(() => {
    const fetchBookmarks = async () => {
      if (currentUserId && hotel) {
        const result = await getBookmarkedHotelIds(currentUserId);
        if (Array.isArray(result)) {
          setIsBookmarked(result.includes(hotel.id));
        }
      }
    };

    fetchBookmarks();
  }, [hotel]);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <ClipLoader size={50} color="#2563EB" />
      </div>
    );
  }

  if (!hotel) {
    return <div className="text-center py-10">Hotel not found.</div>;
  }

  return (
    <div className="p-6">
      <HotelDetails
        hotel={hotel}
        reviews={reviews}
        userInfo={userInfo}
        getPricingRange={getPricingRange}
        formatToAMPM={formatToAMPM}
        renderStars={renderStars}
        isBookmarked={isBookmarked}
        setIsBookmarked={setIsBookmarked}
        handleBookmarkToggle={handleBookmarkToggle}
        handleDeleteHotel={handleDeleteHotel}
        userId={currentUserId!}
        setIsReviewModalOpen={setIsReviewModalOpen}
      />
      <ConfirmationModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onConfirm={confirmDeletion}
        message={modalMessage}
      />
      <MessageModal
        isOpen={messageModalOpen}
        onClose={() => setMessageModalOpen(false)}
        message={messageModalContent}
        type={messageModalType}
      />
      <ReviewModal
        isOpen={isReviewModalOpen}
        onClose={handleCloseReviewModal}
        hotelId={hotel.id}
        userId={Number(currentUserId)}
        onReviewSubmitted={handleReviewSubmitted}
      />
    </div>
  );
};

export default HotelDetailsPage;

import React, { useContext, useState } from "react";
import { useNavigate } from 'react-router-dom';
import LoginForm from "../components/User/LoginForm";
import { AuthContext } from "../contexts/AuthContext";
import { loginUser } from "../services/userApi";
import { LoginData } from "../types/User";

const LoginPage: React.FC = () => {
  const [loginData, setLoginData] = useState<LoginData>({
    email: "",
    password: "",
    role: "customer", // Default to 'customer' role
  });
  const [error, setError] = useState<string | null>(null);
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  // Handle input changes for email, password, and role
  const handleLoginChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setLoginData((prevData) => ({ ...prevData, [name]: value }));
  };

  // Handle login logic (authentication)
  const handleLogin = async (loginData: LoginData) => {
    if (!loginData.email || !loginData.password) {
      setError("Please enter both email and password.");
      return;
    }

    try {
      const { user, token } = await loginUser(loginData); // Your login API call
      login(token, loginData.role, user.id);

      setError(null);
      navigate("/"); // Redirect after successful login
    } catch (err: any) {
      setError(err.message || "An error occurred during login. Please try again.");
    }
  };  

  return (
    <div className="flex justify-center items-center min-h-full bg-gray-100">
      <div className="w-full max-w-screen-md p-6">
        {/* Login Form - Always displayed with the current loginData */}
        <LoginForm
          onLogin={handleLogin}
          error={error}
          loginData={loginData}
          handleChange={handleLoginChange}
        />
      </div>
    </div>
  );
};

export default LoginPage;

import React, { useEffect, useState } from 'react';
import { cancelBooking, getAllBookings } from '../services/bookingApi';
import { fetchHotelById } from '../services/hotelApi';
import { getUserInfo } from '../services/userApi';
import { DetailedBooking } from '../types/Booking';

const ITEMS_PER_PAGE = 8;

const ManageBookingsPage: React.FC = () => {
  const [bookings, setBookings] = useState<DetailedBooking[]>([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [bookingsPerPage] = useState(ITEMS_PER_PAGE); // Changed from 10 to 5
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const result = await getAllBookings(); // Fetch bookings from the API
        const bookingsWithDetails = await Promise.all(result.bookings.map(async (booking) => {
          // Fetch user details
          const userInfo = await getUserInfo(String(booking.userId));
          // Fetch hotel details
          const hotelInfo = await fetchHotelById(booking.hotelId);

          return {
            ...booking,
            userFirstName: userInfo.user.firstName,
            userLastName: userInfo.user.lastName,
            hotelName: hotelInfo.name,
            hotelCheckInTime: hotelInfo.checkIn || 'N/A',
            hotelCheckOutTime: hotelInfo.checkOut || 'N/A'
          };
        }));

        setBookings(bookingsWithDetails);
        setError('');
      } catch (err: any) {
        setError(err.message || 'Failed to load bookings');
      }
    };
    fetchBookings();
  }, []);

  const indexOfLastBooking = currentPage * bookingsPerPage;
  const indexOfFirstBooking = indexOfLastBooking - bookingsPerPage;

  const currentBookings = bookings.slice(indexOfFirstBooking, indexOfLastBooking);

  const totalPages = Math.ceil(bookings.length / bookingsPerPage);

  const paginate = (pageNumber: number) => setCurrentPage(pageNumber);

  const handleCancel = async (bookingId: number) => {
    try {
      await cancelBooking(bookingId);
      setBookings((prevBookings) => prevBookings.filter((booking) => booking.id !== bookingId));
    } catch (err: any) {
      setError(err.message || 'Failed to cancel booking');
    }
  };

  return (
    <div className="p-6 bg-gray-900 text-white min-h-full relative">
      <h1 className="text-2xl mb-4">Manage Bookings</h1>

      {error && <p className="text-red-600 text-center">{error}</p>}

      {bookings.length === 0 ? (
        <div>No bookings found.</div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {currentBookings.map((booking) => (
            <div
              key={booking.id}
              className={`flex flex-col p-3 rounded-lg ${booking.status === 'CANCELLED' ? 'bg-gray-700' : 'bg-gray-800'}`}
            >
              <h2 className="text-lg font-semibold">Booking ID: {booking.id}</h2>
              <p className="text-sm">User: {booking.userFirstName} {booking.userLastName}</p>
              <p className="text-sm">Hotel: {booking.hotelName}</p>
              <p className="text-sm">
                Check-in: {new Date(booking.checkInDate).toLocaleDateString()} at {booking.hotelCheckInTime?.slice(0, 5)}
              </p>
              <p className="text-sm">
                Check-out: {new Date(booking.checkOutDate).toLocaleDateString()} at {booking.hotelCheckOutTime?.slice(0, 5)}
              </p>
              <p className="text-sm">
                <span
                  className={`font-semibold py-1 px-3 rounded-full inline-block ${
                    booking.status === 'CONFIRMED'
                      ? 'bg-green-100 text-green-600'
                      : booking.status === 'CANCELLED'
                      ? 'bg-red-100 text-red-600'
                      : 'bg-yellow-100 text-yellow-600'
                  }`}
                >
                  {booking.status}
                </span>
              </p>

              {booking.status !== 'CANCELLED' && (
                <button
                  onClick={() => handleCancel(booking.id)}
                  className="bg-red-500 text-white px-3 py-1 mt-2 rounded transition-all duration-300 transform hover:bg-red-400 hover:scale-105"
                >
                  Cancel
                </button>
              )}
            </div>
          ))}
        </div>
      )}

      {/* Pagination Controls */}
      {totalPages > 1 && (
      <div className="flex justify-center mt-6 space-x-2">
        <button
          onClick={() => paginate(currentPage - 1)}
          disabled={currentPage === 1}
          className="px-3 py-1 bg-gray-700 hover:bg-gray-600 rounded disabled:opacity-50"
        >
          Prev
        </button>

        {Array.from({ length: totalPages }, (_, i) => i + 1)
          .filter((page) => {
            return (
              page === 1 ||
              page === totalPages ||
              Math.abs(page - currentPage) <= 1
            );
          })
          .reduce<(number | string)[]>((acc, page, index, pages) => {
            if (index > 0 && (page as number) - (pages[index - 1] as number) > 1) {
              acc.push('...');
            }
            acc.push(page);
            return acc;
          }, [])
          .map((item, index) => (
            <button
              key={index}
              onClick={() => typeof item === 'number' && paginate(item)}
              disabled={item === '...'}
              className={`px-3 py-1 rounded ${
                item === currentPage
                  ? 'bg-blue-600'
                  : 'bg-gray-700 hover:bg-gray-600'
              } ${item === '...' && 'cursor-default text-gray-400'}`}
            >
              {item}
            </button>
          ))}

        <button
          onClick={() => paginate(currentPage + 1)}
          disabled={currentPage === totalPages}
          className="px-3 py-1 bg-gray-700 hover:bg-gray-600 rounded disabled:opacity-50"
        >
          Next
        </button>
      </div>
    )}

    </div>
  );
};

export default ManageBookingsPage;

import React, { useState, useEffect } from 'react';
import { getAllUsers, deleteUser, updateUser, registerUser } from '../services/userApi';
import { User, RegisterData } from '../types/User';
import UserModal from '../components/UserModal';
import { Edit, Trash, User as UserIcon, Shield as AdminIcon } from 'lucide-react';
import MessageModal from '../components/MessageModal';

type RegisterResponse = {
  message?: string;
  data: User;
};

type UpdateResponse = {
  message?: string;
  user: User;
};

const ITEMS_PER_PAGE = 6;

const ManageUsers: React.FC = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState<User | null>(null);
  const [error, setError] = useState('');
  const [messageModalOpen, setMessageModalOpen] = useState(false);
  const [messageType, setMessageType] = useState<'success' | 'error'>('success');
  const [messageContent, setMessageContent] = useState('');

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const result = await getAllUsers();
        setUsers(result.users);
        setError('');
      } catch (err: any) {
        setError(err.message || 'Failed to load users');
        setTimeout(() => setError(''), 5000); // Hide error after 5 seconds
      }
    };
    fetchUsers();
  }, []);

  const totalPages = Math.ceil((users.length || 1) / ITEMS_PER_PAGE);
  const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
  const currentUsers = users.slice(startIndex, startIndex + ITEMS_PER_PAGE);

  const goToPage = (page: number) => {
    if (page >= 1 && page <= totalPages) {
      setCurrentPage(page);
    }
  };

  const handleEdit = (user: User) => {
    setCurrentUser(user);
    setIsModalOpen(true);
  };

  const handleDelete = async (userId: number) => {
    try {
      await deleteUser(String(userId));

      setUsers((prev) => {
        const updatedUsers = prev.filter((u) => u.id !== userId);
        const newTotalPages = Math.ceil(updatedUsers.length / ITEMS_PER_PAGE);
        if (currentPage > newTotalPages) {
          setCurrentPage(newTotalPages || 1);
        }
        return updatedUsers;
      });

      setMessageType('success');
      setMessageContent('User deleted successfully!');
      setMessageModalOpen(true);
    } catch (err: any) {
      setError(err.message || 'Failed to delete user');
      setMessageType('error');
      setMessageContent(err.message || 'Failed to delete user');
      setMessageModalOpen(true);
    }
  };  

  const handleSubmit = async (userData: User | RegisterData) => {
    try {
      if (userData.id === 0) {
        const { id, ...newUserData } = userData;
        const response: RegisterResponse = await registerUser(newUserData as RegisterData);

        if (response?.data) {
          setUsers((prev) => [...prev, response.data]);
          setIsModalOpen(false);
          setCurrentUser(null);

          setMessageType('success');
          setMessageContent(response.message || 'User added successfully!');
          setMessageModalOpen(true);

          setTimeout(() => setMessageModalOpen(false), 3000);
        }
      } else {
        const response: UpdateResponse = await updateUser(String(userData.id), userData);

        if (response?.user) {
          setUsers((prev) =>
            prev.map((u) => (u.id === userData.id ? response.user : u))
          );
          setIsModalOpen(false);
          setCurrentUser(null);

          setMessageType('success');
          setMessageContent('User updated successfully!');
          setMessageModalOpen(true);

          setTimeout(() => setMessageModalOpen(false), 3000);
        }
      }
    } catch (err: any) {
      setError(err.message || 'Failed to submit user');
      setMessageType('error');
      setMessageContent(err.message || 'Something went wrong. Please try again.');
      setMessageModalOpen(true);
    }
  };

  const handleAddUser = () => {
    setCurrentUser(null);
    setIsModalOpen(true);
  };

  return (
    <div className="p-8 bg-gray-900 text-white min-h-screen relative">
      <h1 className="text-4xl font-semibold mb-8 text-center">Admin - Manage Users</h1>

      <button
        onClick={handleAddUser}
        className="mb-4 px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-500 transition-all transform hover:scale-110 absolute top-8 right-8"
      >
        Add User
      </button>

      {users.length === 0 ? (
        <p className="text-center text-lg">No users found.</p>
      ) : (
        <ul className="max-w-5xl mx-auto grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {currentUsers.map((user) => (
            <li
              key={user.id}
              className="flex flex-col items-center gap-4 p-4 bg-gray-800 rounded-md shadow-md hover:shadow-xl transition-all duration-300"
            >
              <div className="flex items-center gap-3 text-center w-full">
                <div className="flex items-center gap-2">
                  {user.role === 'ADMIN' ? (
                    <AdminIcon size={18} className="text-blue-500" />
                  ) : (
                    <UserIcon size={18} className="text-green-500" />
                  )}
                </div>
                <p className="font-semibold text-lg text-white flex-shrink-0">
                  {user.firstName} {user.lastName}
                </p>
                <div className="ml-auto flex gap-3">
                  <button
                    onClick={() => handleEdit(user)}
                    className="p-2 bg-green-600 hover:bg-green-500 rounded-md transform hover:scale-110 transition-all"
                    title="Edit"
                  >
                    <Edit size={18} />
                  </button>
                  <button
                    onClick={() => handleDelete(user.id)}
                    className="p-2 bg-red-600 hover:bg-red-500 rounded-md transform hover:scale-110 transition-all"
                    title="Delete"
                  >
                    <Trash size={18} />
                  </button>
                </div>
              </div>
              <p className="text-gray-400">{user.email}</p>
              <p className="text-gray-400">Phone: {user.phoneNumber}</p>
            </li>
          ))}
        </ul>
      )}

      {/* Pagination Controls */}
      {totalPages > 1 && (
        <div className="flex justify-center mt-6 space-x-2">
          <button
            onClick={() => goToPage(currentPage - 1)}
            disabled={currentPage === 1}
            className="px-3 py-1 bg-gray-700 hover:bg-gray-600 rounded disabled:opacity-50"
          >
            Prev
          </button>

          {Array.from({ length: totalPages }, (_, i) => i + 1)
            .filter((page) => {
              return (
                page === 1 || 
                page === totalPages || 
                Math.abs(page - currentPage) <= 1
              );
            })
            .reduce((acc: (number | string)[], page, index, pages) => {
              if (index > 0 && (page as number) - (pages[index - 1] as number) > 1) {
                acc.push('...');
              }
              acc.push(page);
              return acc;
            }, [])
            .map((item, index) => (
              <button
                key={index}
                onClick={() => typeof item === 'number' && goToPage(item)}
                disabled={item === '...'}
                className={`px-3 py-1 rounded ${
                  item === currentPage ? 'bg-blue-600' : 'bg-gray-700 hover:bg-gray-600'
                } ${item === '...' && 'cursor-default text-gray-400'}`}
              >
                {item}
              </button>
            ))}

          <button
            onClick={() => goToPage(currentPage + 1)}
            disabled={currentPage === totalPages}
            className="px-3 py-1 bg-gray-700 hover:bg-gray-600 rounded disabled:opacity-50"
          >
            Next
          </button>
        </div>
      )}


      <UserModal
        isOpen={isModalOpen}
        onClose={() => {
          setIsModalOpen(false);
          setCurrentUser(null);
        }}
        onSubmit={handleSubmit}
        currentUser={currentUser}
      />

      <MessageModal
        isOpen={messageModalOpen}
        onClose={() => setMessageModalOpen(false)}
        type={messageType}
        message={messageContent || error}
      />
    </div>
  );
};

export default ManageUsers;

import { CreditCard } from "lucide-react";
import React, { useEffect, useState } from "react";
import { getPaymentsByUserId } from "../services/paymentApi";
import { Payment } from "../types/Payment";

const ITEMS_PER_PAGE = 5;

const MyPaymentsPage: React.FC = () => {
  const [payments, setPayments] = useState<Payment[]>([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPayments = async () => {
      const storedUserId = sessionStorage.getItem("userId");
      if (!storedUserId) {
        setLoading(false);
        return;
      }

      try {
        const userId = Number(storedUserId);
        setLoading(true);
        const data = await getPaymentsByUserId(userId);
        setPayments(Array.isArray(data) ? data : []);
      } catch (err: any) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchPayments();
  }, []);

  const totalPages = Math.ceil(payments.length / ITEMS_PER_PAGE);
  const currentPayments = payments.slice(
    (currentPage - 1) * ITEMS_PER_PAGE,
    currentPage * ITEMS_PER_PAGE
  );

  const paginate = (pageNumber: number) => setCurrentPage(pageNumber);
  const prevPage = () => currentPage > 1 && setCurrentPage(currentPage - 1);
  const nextPage = () => currentPage < totalPages && setCurrentPage(currentPage + 1);

  if (loading) return <div className="p-6 text-white">Loading payments...</div>;

  return (
    <div className="bg-gray-900 min-h-full py-8 px-4 sm:px-6 lg:px-12">
      <h2 className="text-gray-100 text-3xl font-semibold text-center mb-8">My Payments</h2>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {payments.length === 0 && !loading ? (
          <p className="text-gray-500 text-center col-span-full">No payments found.</p>
        ) : (
          currentPayments.map((payment) => (
            <div
              key={payment.id}
              className="p-6 rounded-xl bg-gradient-to-br from-gray-800 to-gray-700 shadow-lg text-gray-200 flex flex-col gap-4"
            >
              <div className="flex items-center gap-3">
                <CreditCard size={28} className="text-blue-400" />
                <div>
                  <div className="text-lg font-semibold">Booking #{payment.bookingId}</div>
                  <div className="text-sm text-gray-400">{new Date(payment.transactionDate).toLocaleString()}</div>
                </div>
              </div>

              <div className="flex justify-between text-base font-medium">
                <span>Amount</span>
                <span className="text-blue-300">${payment.amount.toFixed(2)}</span>
              </div>

              <div className="flex justify-between text-base font-medium">
                <span>Status</span>
                <span
                  className={`text-xs font-bold px-3 py-1 rounded-full ${
                    payment.status === "SUCCESS"
                      ? "bg-green-200 text-green-800"
                      : payment.status === "FAILURE"
                      ? "bg-red-200 text-red-800"
                      : "bg-yellow-200 text-yellow-800"
                  }`}
                >
                  {payment.status}
                </span>
              </div>
            </div>
          ))
        )}
      </div>

      {payments.length > 0 && (
        <div className="flex justify-center mt-6 items-center gap-2">
          <button
            onClick={prevPage}
            disabled={currentPage === 1}
            className={`px-4 py-2 rounded-md ${
              currentPage === 1
                ? "bg-gray-300 text-gray-800 cursor-not-allowed"
                : "bg-blue-600 text-white hover:bg-blue-500 transition"
            }`}
          >
            &lt;
          </button>

          {Array.from({ length: totalPages }, (_, index) => (
            <button
              key={index + 1}
              onClick={() => paginate(index + 1)}
              className={`px-4 py-2 rounded-md ${
                currentPage === index + 1
                  ? "bg-blue-600 text-white"
                  : "bg-gray-300 text-gray-800 hover:bg-gray-200 transition"
              }`}
            >
              {index + 1}
            </button>
          ))}

          <button
            onClick={nextPage}
            disabled={currentPage === totalPages}
            className={`px-4 py-2 rounded-md ${
              currentPage === totalPages
                ? "bg-gray-300 text-gray-800 cursor-not-allowed"
                : "bg-blue-600 text-white hover:bg-blue-500 transition"
            }`}
          >
            &gt;
          </button>
        </div>
      )}
    </div>
  );
};

export default MyPaymentsPage;

import { Bell, CheckCircle } from "lucide-react";
import React, { useEffect, useState } from "react";
import { useNotificationContext } from "../contexts/NotificationContext";
import NotificationApi from "../services/notificationApi";

const ITEMS_PER_PAGE = 6;

const NotificationsPage: React.FC = () => {
  const [userId, setUserId] = useState<number | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [markingId, setMarkingId] = useState<number | null>(null);

  // Use the notification context
  const { notifications, refreshNotifications } = useNotificationContext();

  useEffect(() => {
    const storedUserId = sessionStorage.getItem("userId");
    if (storedUserId) {
      setUserId(Number(storedUserId));
    } else {
      setError("User not logged in.");
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    if (userId !== null) {
      const fetchNotifications = async () => {
        try {
          await NotificationApi.getNotificationsByUserId(userId);
        } catch (err) {
          setError("Failed to load notifications.");
        } finally {
          setLoading(false);
        }
      };
      fetchNotifications();
    }
  }, [userId]);

  const markAsRead = async (notificationId: number) => {
    try {
      setMarkingId(notificationId);
      await NotificationApi.markNotificationAsRead(notificationId);
      // After marking as read, refresh notifications
      await refreshNotifications();
    } catch (err) {
      setError("Failed to mark notification as read.");
    } finally {
      setMarkingId(null);
    }
  };

  const markAllAsRead = async () => {
    try {
      // Mark all unread notifications as read
      const unreadNotifications = notifications.filter((n) => !n.isread);
      for (const notification of unreadNotifications) {
        await NotificationApi.markNotificationAsRead(notification.notificationId);
      }
      await refreshNotifications(); // Refresh the notifications list
    } catch (err) {
      setError("Failed to mark all notifications as read.");
    }
  };

  const sortedNotifications = [...notifications].sort((a, b) => {
    if (a.isread === b.isread) {
      // Both read or both unread  sort by createdAt descending
      return new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime();
    }
    // Unread first
    return a.isread ? 1 : -1;
  });

  const totalPages = Math.ceil(sortedNotifications.length / ITEMS_PER_PAGE);
  const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
  const currentNotifications = sortedNotifications.slice(
    startIndex,
    startIndex + ITEMS_PER_PAGE
  );


  const goToPage = (page: number) => {
    if (page >= 1 && page <= totalPages) {
      setCurrentPage(page);
    }
  };

  if (loading) return <div>Loading notifications...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="relative p-6 bg-gray-900 text-white min-h-full">
      {/* Title & Action */}
      <div className="mb-4">
        <h1 className="text-2xl font-semibold">Notifications</h1>
      </div>

      {/* Mark All as Read Button */}
      <button
        onClick={markAllAsRead}
        className="absolute top-6 right-6 px-4 py-2 bg-blue-600 text-white rounded-lg shadow-md hover:bg-blue-500 hover:scale-105 active:scale-95 transition-all transform focus:outline-none focus:ring-2 focus:ring-blue-400 flex items-center"
      >
        <CheckCircle size={20} className="mr-2 text-white" />
        Mark All as Read
      </button>


      {/* Notification List */}
      {currentNotifications.length === 0 ? (
        <div className="text-gray-400 text-center mt-20 text-sm">
          You have no notifications.
        </div>
      ) : (
        <ul className="space-y-3">
          {currentNotifications.map((notification) => (
            <li
              key={notification.notificationId}
              className={`flex items-start gap-2 p-3 rounded-lg shadow-sm transition ${
                notification.isread ? "bg-gray-800" : "bg-gray-700"
              }`}
            >
              <Bell size={18} className="mt-0.5 text-yellow-400" />
              <div className="flex-1">
                <p className="text-sm">{notification.message}</p>
                <p className="text-xs text-gray-400 mt-0.5">
                  {new Date(notification.createdAt).toLocaleString()}
                </p>
              </div>
              {!notification.isread && (
                <button
                  onClick={() => markAsRead(notification.notificationId)}
                  disabled={markingId === notification.notificationId}
                  className="text-green-500 hover:text-green-400 disabled:opacity-50 transition"
                  aria-label="Mark as read"
                >
                  <CheckCircle size={16} />
                </button>
              )}
            </li>
          ))}
        </ul>
      )}

      {/* Pagination Controls */}
      {totalPages > 1 && (
        <div className="flex justify-center mt-6 space-x-1 text-sm">
          <button
            onClick={() => goToPage(currentPage - 1)}
            disabled={currentPage === 1}
            className="px-3 py-1 bg-gray-700 hover:bg-gray-600 rounded disabled:opacity-50"
          >
            Prev
          </button>
          {Array.from({ length: totalPages }, (_, i) => (
            <button
              key={i + 1}
              onClick={() => goToPage(i + 1)}
              className={`px-3 py-1 rounded ${
                currentPage === i + 1
                  ? "bg-blue-600"
                  : "bg-gray-700 hover:bg-gray-600"
              }`}
            >
              {i + 1}
            </button>
          ))}
          <button
            onClick={() => goToPage(currentPage + 1)}
            disabled={currentPage === totalPages}
            className="px-3 py-1 bg-gray-700 hover:bg-gray-600 rounded disabled:opacity-50"
          >
            Next
          </button>
        </div>
      )}
    </div>
  );  

};

export default NotificationsPage;

import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import MessageModal from "../components/MessageModal";
import { useNotificationContext } from "../contexts/NotificationContext";
import { getBookingById } from "../services/bookingApi";
import { fetchHotelById } from "../services/hotelApi";
import { createAndProcessPayment, getPaymentsByBookingId } from "../services/paymentApi";

const PaymentPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { bookingId } = location.state || {};

  const { refreshNotifications } = useNotificationContext();

  const [cardNumber, setCardNumber] = useState("");
  const [expiry, setExpiry] = useState("");
  const [cvv, setCvv] = useState("");
  const [amountPaidNow, setAmountPaidNow] = useState<number>(0);
  const [amountAlreadyPaid, setAmountAlreadyPaid] = useState<number>(0);
  const [isLoading, setIsLoading] = useState(false);
  const [amountError, setAmountError] = useState("");

  const [bookingDetails, setBookingDetails] = useState<any>(null);
  const [hotelDetails, setHotelDetails] = useState<any>(null);

  const [modalOpen, setModalOpen] = useState(false);
  const [modalMessage, setModalMessage] = useState("");
  const [modalType, setModalType] = useState<"success" | "error">("success");

  useEffect(() => {
    const fetchData = async () => {
      if (!bookingId) {
        navigate("/bookings");
        return;
      }

      try {
        const booking = await getBookingById(bookingId);
        setBookingDetails(booking);

        const hotel = await fetchHotelById(booking.hotelId);
        setHotelDetails(hotel);

        try {
          const payments = await getPaymentsByBookingId(bookingId);
          const totalPaid = payments?.length
            ? payments.reduce((sum, payment) => sum + Number(payment.amountPaid), 0)
            : 0;
          setAmountAlreadyPaid(totalPaid);
        } catch (paymentError) {
          console.warn("No payments found or error retrieving payments:", paymentError);
          setAmountAlreadyPaid(0);
        }
      } catch (error) {
        console.error("Error fetching booking/hotel info:", error);
        navigate("/bookings");
      }
    };

    fetchData();
  }, [bookingId, navigate]);

  const handleAmountChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = Number(e.target.value);
    setAmountPaidNow(value);

    if (value < 0) {
      setAmountError("Amount cannot be negative.");
    } else if (value > remainingAmount) {
      setAmountError("Amount exceeds the outstanding balance.");
    } else if (value === 0) {
      setAmountError("Amount must be greater than zero.");
    } else {
      setAmountError("");
    }
  };

  const handlePayment = async () => {
    if (!bookingId || !amountPaidNow || !cardNumber || !expiry || !cvv || amountError) {
      setModalType("error");
      setModalMessage("Please fill in all fields correctly.");
      setModalOpen(true);
      return;
    }

    setIsLoading(true);
    try {
      const paymentRequest = {
        bookingId,
        amount: amountPaidNow,
      };

      await createAndProcessPayment(paymentRequest, "CREDIT_CARD");

      setModalType("success");
      setModalMessage("Payment successful!");
      setModalOpen(true);

      // Refresh notifications after payment success
      refreshNotifications();

      setTimeout(() => {
        setModalOpen(false);
        navigate("/bookings");
      }, 2000);
    } catch (error: any) {
      console.error("Payment error:", error);
      setModalType("error");
      setModalMessage(`Payment failed: ${error.message}`);
      setModalOpen(true);
    } finally {
      setIsLoading(false);
    }
  };

  if (!bookingDetails || !hotelDetails) {
    return <div className="text-center mt-10">Loading booking details...</div>;
  }

  const totalAmount = bookingDetails.totalAmount;
  const remainingAmount = totalAmount - amountAlreadyPaid;

  return (
    <div className="flex justify-center items-center min-h-full bg-gray-100 py-12 px-4">
      <div className="w-full max-w-lg p-8 bg-white rounded-xl shadow-lg space-y-6">
        {/* Payment Header */}
        <h2 className="text-3xl font-bold text-gray-800">Hotel Payment</h2>

        {/* Booking Summary */}
        <div className="bg-gray-50 rounded-lg p-6 border border-gray-200">
          <h3 className="text-lg font-semibold text-gray-800 mb-4">Booking Summary</h3>
          <div className="grid grid-cols-2 gap-y-3 text-sm text-gray-700">
            <div className="font-medium">Hotel:</div>
            <div className="text-gray-800">{hotelDetails.name}</div>

            <div className="font-medium">Total Amount:</div>
            <div className="text-gray-800">${totalAmount.toFixed(2)}</div>

            <div className="font-medium">Already Paid:</div>
            <div className="text-gray-800">${amountAlreadyPaid.toFixed(2)}</div>

            <div className="font-medium text-red-600">Outstanding:</div>
            <div className="text-red-600 font-semibold">${remainingAmount.toFixed(2)}</div>
          </div>
        </div>

        {/* Amount Youre Paying Now */}
        <div>
          <label className="block text-gray-700 font-medium mb-2">Amount Youre Paying Now</label>
          <input
            type="text"
            placeholder="Enter payment amount"
            value={amountPaidNow}
            onChange={handleAmountChange}
            className="w-full p-4 bg-gray-100 border border-gray-300 rounded-lg text-lg text-gray-800 focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
          />
          {amountError && <p className="text-red-600 text-sm mt-2">{amountError}</p>}
        </div>

        {/* Card Details Section */}
        <div className="space-y-4">
          <h3 className="text-xl font-semibold text-gray-800">Card Details</h3>

          <input
            type="text"
            placeholder="Card Number"
            value={cardNumber}
            onChange={(e) => setCardNumber(e.target.value)}
            className="w-full p-4 bg-gray-100 border border-gray-300 rounded-lg text-lg text-gray-800 focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
          />

          <div className="grid grid-cols-2 gap-4">
            <input
              type="text"
              placeholder="MM/YY"
              value={expiry}
              onChange={(e) => setExpiry(e.target.value)}
              className="w-full p-4 bg-gray-100 border border-gray-300 rounded-lg text-lg text-gray-800 focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
            />
            <input
              type="text"
              placeholder="CVV"
              value={cvv}
              onChange={(e) => setCvv(e.target.value)}
              className="w-full p-4 bg-gray-100 border border-gray-300 rounded-lg text-lg text-gray-800 focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
            />
          </div>
        </div>

        {/* Payment Button */}
        <button
          onClick={handlePayment}
          disabled={isLoading || !!amountError}
          className="w-full py-3 bg-blue-600 text-white rounded-full hover:bg-blue-700 transition-all transform hover:scale-105"
        >
          {isLoading ? "Processing..." : "Confirm Payment"}
        </button>

        {/* Modal for Payment Feedback */}
        <MessageModal
          isOpen={modalOpen}
          onClose={() => setModalOpen(false)}
          message={modalMessage}
          type={modalType}
        />
      </div>
    </div>
  );

};

export default PaymentPage;

import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { RegisterData } from "../types/User";
import RegisterForm from "../components/User/RegisterForm";
import { registerUser } from "../services/userApi";

const RegisterPage: React.FC = () => {
  const [registerData, setRegisterData] = useState<RegisterData>({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    phoneNumber: "",
    role: "CUSTOMER", // Default role set to uppercase
  });
  const [confirmPassword, setConfirmPassword] = useState(""); // State for confirm password
  const [error, setError] = useState<string | null>(null);
  const navigate = useNavigate();

  const handleRegisterChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setRegisterData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleRegister = async (registerData: RegisterData) => {
    if (!registerData.firstName || !registerData.lastName || !registerData.email || !registerData.password || !registerData.phoneNumber) {
      setError("Please fill in all fields.");
      return;
    }

    // Check if the passwords match
    if (registerData.password !== confirmPassword) {
      setError("Passwords do not match.");
      return;
    }

    try {
      const data = await registerUser(registerData);

      if (data) {
        setError(null);
        navigate("/login");
      }
    } catch (err: any) {
      setError(err.message || "An error occurred during registration. Please try again.");
    }
  };

  return (
    <div className="flex justify-center items-center min-h-full bg-gray-100">
      <div className="w-full max-w-screen-md p-6">
        <RegisterForm
          onRegister={handleRegister}
          error={error}
          registerData={registerData}
          handleChange={handleRegisterChange}
          confirmPassword={confirmPassword} // Pass confirmPassword state
          setConfirmPassword={setConfirmPassword} // Pass setConfirmPassword function
        />
      </div>
    </div>
  );
};

export default RegisterPage;

import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { verifyUser } from "../services/userApi";

const VerifyEmailPage: React.FC = () => {
  const [message, setMessage] = useState<string | null>(null);
  const [isSuccess, setIsSuccess] = useState<boolean | null>(null);
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [tokenInput, setTokenInput] = useState<string>("");
  const navigate = useNavigate();

  const handleVerify = async () => {
    if (!tokenInput.trim()) {
      setIsSuccess(false);
      setMessage("Please enter a valid verification token.");
      return;
    }

    try {
      setIsLoading(true);
      const response = await verifyUser(tokenInput);
      console.log("API response:", response);
      setIsSuccess(true);
      setMessage("Your email has been successfully verified! Redirecting to login...");

      setTimeout(() => navigate("/login"), 5000);
    } catch (error: any) {
      console.error("Verification error:", error);
      setIsSuccess(false);
      setMessage(error.message || "Verification failed. The token may be invalid or expired.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-md text-center max-w-md w-full">
        <h1 className={`text-2xl font-semibold mb-4 ${isSuccess ? "text-green-600" : isSuccess === false ? "text-red-600" : ""}`}>
          {isLoading ? "Verifying..." : isSuccess === null ? "Email Verification" : isSuccess ? "Success!" : "Verification Failed"}
        </h1>

        <p className="text-gray-700 mb-4">
          {isLoading
            ? "Please wait while we verify your email..."
            : message || "Enter your verification token below."}
        </p>

        {!isLoading && (
          <>
            <input
              type="text"
              placeholder="Enter token"
              className="w-full px-4 py-2 border rounded-md mb-4"
              value={tokenInput}
              onChange={(e) => setTokenInput(e.target.value)}
            />
            <button
              onClick={handleVerify}
              className="w-full px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-500"
            >
              Verify Email
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default VerifyEmailPage;

import React from "react";
import { FaBookmark, FaRegBookmark } from "react-icons/fa"; // Import missing icons
import { useNavigate } from "react-router-dom"; // Import useNavigate
import { HotelData } from "../../types/Hotels";
import { Review } from "../../types/Review";

type HotelDetailsProps = {
  hotel: HotelData | null;
  reviews: Review[];
  userInfo: { [key: string]: { firstName: string; lastName: string } };
  getPricingRange: () => string;
  formatToAMPM: (timeString: string) => string;
  renderStars: (rating: number) => React.ReactNode;
  isBookmarked: boolean;
  setIsBookmarked: (value: boolean) => void;
  handleBookmarkToggle: () => void;
  handleDeleteHotel: (hotelId: number) => void;
  setIsReviewModalOpen: (open: boolean) => void;
  userId: Number;
};

const HotelDetails: React.FC<HotelDetailsProps> = ({
  hotel,
  reviews,
  userInfo,
  getPricingRange,
  formatToAMPM,
  renderStars,
  isBookmarked,
  handleBookmarkToggle,
  handleDeleteHotel,
  setIsReviewModalOpen,
