          {/* Book Button (only for non-admins) */}
          {userId && (
            <button
              onClick={handleBookClick}
              className="bg-blue-600 text-white text-sm px-4 py-2 rounded-full shadow-md hover:bg-blue-700 transition-all transform hover:scale-105"            >
