  const response = await fetch(`${API_BASE_URL}/verify`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ token }),
  });

  const message = await response.text();

  if (response.ok) {
    return message;
  } else {
    throw new Error(message);
  }
};
